package Programs;

public class SubstringDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name="Rose Mary Joseph";
		System.out.println("original string:"+name);
		System.out.println("first name:"+name.substring(0,9));   
		System.out.println("last name:"+name.substring(10,16));   
	}

}
